mapper.update:
update users u
set u.user_mileage = u.user_mileage + (
    select sum(p.pay_amount)
    from payment p
    where p.ptype_code = 2
      and p.res_code in (
        select r.res_code
        from reservation r
        where r.user_id = #{user_id}
    )
)
where u.user_id = #{user_id}

mapper.delete:
delete from reservation
where user_id = #{user_id}
  and res_code = #{res_code}
