mapper.select:
select *
from air_route_schedule
where dpt_apt_code = #{dptAptCode}
  and arr_apt_code = #{arrAptCode}
order by DPT_TIME

with air_a as (
select a.AIR_CODE , c.AIR_NAME , a.FLT_CODE
from AIR_ROUTE_SCHEDULE a, AIRCRAFT c
where a.AIR_CODE = c.AIR_CODE
and a.FLT_CODE = #{fltCode}
),
air_b as (
select *
from PASSENGER
where USER_ID = #{userId}
and user_name in

    #{item}

and FLT_CODE = #{fltCode}
)

SELECT air_a.*, air_b.*
FROM air_b
JOIN air_a ON air_a.FLT_CODE = air_b.FLT_CODE
