mapper.select:
select count(*)
  from users
 where user_id = #{userId}
  and user_pw = #{userPw}

select user_mileage
from users
where user_id = #{userId}

mapper.update:
UPDATE users
SET user_mileage = #{userMileage}
WHERE user_id = #{userId}

mapper.select:
select count(*)
from users
where user_id = #{user_id}

SELECT USER_ID
	 , USER_PW
	 , USER_NAME
	 , USER_EMAIL
	 , USER_REGNUM
FROM users
WHERE user_id = #{userId}
  AND user_regnum = #{userRegnum}
  AND user_email = #{userEmail}

SELECT USER_ID
	 , USER_PW
	 , USER_NAME
	 , USER_EMAIL
	 , USER_REGNUM
FROM users
WHERE USER_NAME = #{userName}
  AND USER_REGNUM = #{userRegnum}
  AND USER_EMAIL = #{userEmail}
